import axios from "axios";
import React from "react";
import { useState } from "react";
import Swal from "react-sweetalert2";

function Workout()
{
   
    const [name,setname]= useState('');
    const [email,setemail]= useState('');
    const [date,setdate]= useState('');
    const [workout,setworkout]= useState('');
    const [message,setmessage]= useState('');

const workoutrecord = () =>
{

    if(name == "")
    {
        Swal.fire({
            title: "Validation Message",
            text: "Name Field iS Required*",
            icon: "wrong"
          });
    }
    else if(email == "")
    {
        Swal.fire({
            title: "Validation Message",
            text: "Email Field iS Required*",
            icon: "wrong"
          });
    } 
      else if(date == "")
    {
        Swal.fire({
            title: "Validation Message",
            text: "Date Field iS Required*",
            icon: "wrong"
          });
    }   
    else if(workout == "")
    {
        Swal.fire({
            title: "Validation Message",
            text: "Workout Field iS Required*",
            icon: "wrong"
          });
    } 
     else if(message == "")
    {
        Swal.fire({
            title: "Validation Message",
            text: "Message Field iS Required*",
            icon: "wrong"
          });
    }
    else(

    axios.post('http://localhost:4000/workout',{name,email,date,workout,message})
    )
}

    return(
        <div>

<div class="container-fluid p-0 nav-bar">
        <nav class="navbar navbar-expand-lg bg-none navbar-dark py-3">
            <a href="" class="navbar-brand">
                <h1 class="m-0 display-4 font-weight-bold text-uppercase text-white">Gymnast</h1>
            </a>
            <button type="button" class="navbar-toggler" data-toggle="collapse" data-target="#navbarCollapse">
                <span class="navbar-toggler-icon"></span>
            </button>
            <div class="collapse navbar-collapse justify-content-between" id="navbarCollapse">
                <div class="navbar-nav ml-auto p-4 bg-secondary">
                    <a href="index.html" class="nav-item nav-link">Home</a>
                    <a href="about.html" class="nav-item nav-link">About Us</a>
                    <a href="feature.html" class="nav-item nav-link">Our Features</a>
                    <a href="class.html" class="nav-item nav-link">Classes</a>
                    <div class="nav-item dropdown">
                        <a href="#" class="nav-link dropdown-toggle" data-toggle="dropdown">Pages</a>
                        <div class="dropdown-menu text-capitalize">
                            <a href="blog.html" class="dropdown-item">Blog Grid</a>
                            <a href="single.html" class="dropdown-item">Blog Detail</a>
                        </div>
                    </div>
                    <a href="contact.html" class="nav-item nav-link active">Contact</a>
                </div>
            </div>
        </nav>
    </div>
   
    <div class="container-fluid page-header mb-5">
        <div class="d-flex flex-column align-items-center justify-content-center pt-0 pt-lg-5" >
            <h4 class="display-4 mb-3 mt-0 mt-lg-5 text-white text-uppercase font-weight-bold">Work Out  </h4>
            <div class="d-inline-flex">
                <p class="m-0 text-white"><a class="text-white" href="">Home</a></p>
                <p class="m-0 text-white px-2">/</p>
                <p class="m-0 text-white">Work Out   </p>
            </div>
        </div>
    </div>
   
  <div className="container">
  <input type="name" placeholder="Name"   onChange={(e)=>setname(e.target.value)} className="form-control w-50 mt-3"/>
    <input type="email" placeholder="Email"  onChange={(e)=>setemail(e.target.value)}  className="form-control w-50 mt-3"/>
    <label for="exampleInputEmail1">Workout Date</label>
    <input type="date" onChange={(e)=>setdate(e.target.value)}   className="form-control w-50 mt-3"/>
    <div class="form-group">
    <label for="exampleFormControlSelect1">Workout Type</label>
    <select class="form-control" onChange={(e)=>setworkout(e.target.value)} id="exampleFormControlSelect1">
      <option>Cardio</option>
      <option>Strength</option>
      <option>Flexibility</option>
      <option>Balance</option>
      
    </select>
    <template>
  <div>
  </div>
</template>
  </div>

   <div class="control-group">
                            <textarea class="form-control" rows="6" 
                            id="message" placeholder="Message" required="required"  onChange={(e)=>setmessage(e.target.value)}
                           ></textarea>
                            <p class="help-block text-danger"></p>
                        </div>
                        <div>
                            <button class="btn btn-outline-primary"
                             type="submit" onClick={workoutrecord}>Send Message</button>
                        </div>

 <button  className="btn btn-danger mt-3">Log Fitness Progress</button>
  </div>
 
    <a href="#" class="btn btn-outline-primary back-to-top"><i class="fa fa-angle-double-up"></i></a>


        </div>
    )
}

export default Workout
