import axios from "axios";
import React from "react";
import { useState } from "react";
import { useNavigate } from "react-router-dom";
import Swal from "sweetalert2";


function Signup()
{
    const [name,setname] = useState('');
    const [email,setemail] = useState('');
    const [pass,setpass] = useState('');
    const [number,setnumber] = useState('');
    const [address,setaddress] = useState('');
    const [starttime,setstarttime] = useState('');
    const [endtime,setendtime] = useState('');
    const [file,setfile] = useState('');

    const navigate = useNavigate();

    const signuprecord = async () =>
    {
        const formdata = new FormData();
  formdata.append("name",name);
  formdata.append("email",email)
  formdata.append("pass",pass)
  formdata.append("number",number);
  formdata.append("address",address)
  formdata.append("starttime",starttime)
  formdata.append("endtime",endtime);
  formdata.append("file",file)


  if(name == "")
  {
      Swal.fire({
          title: "Validation Message",
          text: "Name Field is Required*",
          icon: "wrong"
        });
  }
  else if(email == "" )
  {
      Swal.fire({
          title: "Validation Message",
          text: "Email Field is Required*",
          icon: "wrong"
        });
  } 
  else if(email.includes('') )
  {
      Swal.fire({
          title: "Validation Message",
          text: "Email Field is Required*",
          icon: "wrong"
        });
  } 
  else if(pass == "")
  {
      Swal.fire({
          title: "Validation Message",
          text: "Password Field is Required*",
          icon: "wrong"
        });
  } 
  else if(number == "")
  {
      Swal.fire({
          title: "Validation Message",
          text: "Number Field is Required*",
          icon: "wrong"
        });
  } 
  else if(address == "")
  {
      Swal.fire({
          title: "Validation Message",
          text: "Address Field is Required*",
          icon: "wrong"
        });
  } 
  else if(starttime == "")
  {
      Swal.fire({
          title: "Validation Message",
          text: "Start time is Required*",
          icon: "wrong"
        });
  } 
  else if(endtime == "")
  {
      Swal.fire({
          title: "Validation Message",
          text: "End time is Required*",
          icon: "wrong"
        });
  } 
  else if(file == "")
  {
      Swal.fire({
          title: "Validation Message",
          text: "File is Required*",
          icon: "wrong"
        });
  } 
  else{


    await axios.post('http://localhost:4000/signup',
    formdata,{
        headers : {
            "Content-Type"  : "multipart/form-data"
        }
    
    })

    navigate('/login')
  }



    }
    return(
        <div>

<div class="container-fluid p-0 nav-bar">
        <nav class="navbar navbar-expand-lg bg-none navbar-dark py-3">
            <a href="" class="navbar-brand">
                <h1 class="m-0 display-4 font-weight-bold text-uppercase text-white">Gymnast</h1>
            </a>
            <button type="button" class="navbar-toggler" data-toggle="collapse" data-target="#navbarCollapse">
                <span class="navbar-toggler-icon"></span>
            </button>
            <div class="collapse navbar-collapse justify-content-between" id="navbarCollapse">
                <div class="navbar-nav ml-auto p-4 bg-secondary">
                    <a href="index.html" class="nav-item nav-link">Home</a>
                    <a href="about.html" class="nav-item nav-link">About Us</a>
                    <a href="feature.html" class="nav-item nav-link">Our Features</a>
                    <a href="class.html" class="nav-item nav-link">Classes</a>
                    <div class="nav-item dropdown">
                        <a href="#" class="nav-link dropdown-toggle" data-toggle="dropdown">Pages</a>
                        <div class="dropdown-menu text-capitalize">
                            <a href="blog.html" class="dropdown-item">Blog Grid</a>
                            <a href="single.html" class="dropdown-item">Blog Detail</a>
                        </div>
                    </div>
                    <a href="contact.html" class="nav-item nav-link active">Contact</a>
                </div>
            </div>
        </nav>
    </div>
   
    <div class="container-fluid page-header mb-5">
        <div class="d-flex flex-column align-items-center justify-content-center pt-0 pt-lg-5" >
            <h4 class="display-4 mb-3 mt-0 mt-lg-5 text-white text-uppercase font-weight-bold">Sign Up</h4>
            <div class="d-inline-flex">
                <p class="m-0 text-white"><a class="text-white" href="">Home</a></p>
                <p class="m-0 text-white px-2">/</p>
                <p class="m-0 text-white">Sign Up </p>
            </div>
        </div>
    </div>
   
  <div className="container">
    <input type="text"onChange={(e)=>setname(e.target.value)} placeholder="Name" className="form-control w-50 mt-3"/>
    <input type="email" placeholder="Email" onChange={(e)=>setemail(e.target.value)} className="form-control w-50 mt-3"/>
    <input type="password" placeholder="Password" onChange={(e)=>setpass(e.target.value)} className="form-control w-50 mt-3"/>
    <input type="text" placeholder="Contact Number" onChange={(e)=>setnumber(e.target.value)} className="form-control w-50 mt-3"/>
    <input type="text" placeholder="Address" onChange={(e)=>setaddress(e.target.value)} className="form-control w-50 mt-3"/>
<label className="mt-3">Start Time</label>
<input className="form-control w-50 mt-3" onChange={(e)=>setstarttime(e.target.value)} type="time"/>
<label className="mt-3">End Time</label>
<input className="form-control w-50 mt-3" onChange={(e)=>setendtime(e.target.value)} type="time"/>
 <input type="file" onChange={(e)=>setfile(e.target.files[0])}  className="form-control w-50 mt-3"/>

 <button onClick={signuprecord} className="btn btn-danger mt-3">Signup</button>
  </div>
  
  <div class="footer container-fluid mt-5 py-5 px-sm-3 px-md-5 text-white">
        <div class="row pt-5">
            <div class="col-lg-3 col-md-6 mb-5">
                <h4 class="text-primary mb-4">Get In Touch</h4>
                <p><i class="fa fa-map-marker-alt mr-2"></i>123 Street, New York, USA</p>
                <p><i class="fa fa-phone-alt mr-2"></i>+012 345 67890</p>
                <p><i class="fa fa-envelope mr-2"></i>info@example.com</p>

            </div>
            <div class="col-lg-3 col-md-6 mb-5">
                <h4 class="text-primary mb-4">Quick Links</h4>
                <div class="d-flex flex-column justify-content-start">
                    <a class="text-white mb-2" href="#"><i class="fa fa-angle-right mr-2"></i>Home</a>
                    <a class="text-white mb-2" href="#"><i class="fa fa-angle-right mr-2"></i>About Us</a>
                    <a class="text-white mb-2" href="#"><i class="fa fa-angle-right mr-2"></i>Our Features</a>
                    <a class="text-white mb-2" href="#"><i class="fa fa-angle-right mr-2"></i>Classes</a>
                    <a class="text-white" href="#"><i class="fa fa-angle-right mr-2"></i>Contact Us</a>
                </div>
            </div>
            <div class="col-lg-3 col-md-6 mb-5">
                <h4 class="text-primary mb-4">Popular Links</h4>
                <div class="d-flex flex-column justify-content-start">
                    <a class="text-white mb-2" href="#"><i class="fa fa-angle-right mr-2"></i>Home</a>
                    <a class="text-white mb-2" href="#"><i class="fa fa-angle-right mr-2"></i>About Us</a>
                    <a class="text-white mb-2" href="#"><i class="fa fa-angle-right mr-2"></i>Our Features</a>
                    <a class="text-white mb-2" href="#"><i class="fa fa-angle-right mr-2"></i>Classes</a>
                    <a class="text-white" href="#"><i class="fa fa-angle-right mr-2"></i>Contact Us</a>
                </div>
            </div>
            <div class="col-lg-3 col-md-6 mb-5">
                <h4 class="text-primary mb-4">Opening Hours</h4>
                <h5 class="text-white">Monday - Friday</h5>
                <p>8.00 AM - 8.00 PM</p>
                <h5 class="text-white">Saturday - Sunday</h5>
                <p>2.00 PM - 8.00 PM</p>
            </div>
        </div>
        <div class="container border-top border-dark pt-5">
            <p class="m-0 text-center text-white">
                &copy; <a class="text-white font-weight-bold" href="#">Your Site Name</a>. All Rights Reserved. Designed by
                <a class="text-white font-weight-bold" href="https://htmlcodex.com">HTML Codex</a>
            </p>
        </div>
    </div>
 
    <a href="#" class="btn btn-outline-primary back-to-top"><i class="fa fa-angle-double-up"></i></a>

        </div>
    )
}

export default Signup;
